import React, { useRef, useState } from 'react';
import ReactMapGL, { FullscreenControl, Marker } from 'react-map-gl';


const DeviceLocation = ({ data }) => {
  console.log(data)
  const mapRef = useRef();
  const [viewport, setViewport] = useState({
    width: "50%",
    height: '805px',
    latitude: 21.437,
    longitude: 105.123,
    zoom: 16
  });
  const fullscreenControlStyle = {
    right: 10,
    bottom: 10
  };


  return <>
    <ReactMapGL
      ref={mapRef}
      {...viewport}
      mapStyle="mapbox://styles/mapbox/streets-v11"
      onViewportChange={nextViewport => setViewport(nextViewport)}
      mapboxApiAccessToken={process.env.REACT_APP_MAP_API_TOKEN}>

      <Marker
        key={data?._id || "Hanoi"}
        longitude={data?.location[0] || 105.123}
        latitude={ data?.location[1]  || 21.437}
      >
        <div
        style={{ background: '#FFA35C', borderRadius: '50%', width: '50px', height: '50px', paddingTop: '10px', color:'white' }}>
        {data?.deviceName}</div>
      </Marker>

      <FullscreenControl style={fullscreenControlStyle} />
    </ReactMapGL>
  </>;
}
export default DeviceLocation
